# Each food's purchased amount should be no less than its minimum required amount
for j in range(FoodCount):
    model.addConstr(AmountFood[j] >= FoodMin[j], name=f"min_food_purchase_{j}")

# Add constraints for non-negativity of food quantities
for i in range(FoodCount):
    model.addConstr(AmountFood[i] >= 0, name="non_negativity_food_{}".format(i))
