constraints.code:
# Each process execution number must be non-negative
for l in range(L):
    model.addConstr(ProcessExecutions[l] >= 0, name=f"ProcessExecutions_nonneg_{l}")

# Add constraints to ensure total resource allocation for all processes does not exceed allocated resources for each option
for o in range(O):
    model.addConstr(gp.quicksum(Input[l, o] * ProcessExecutions[l] for l in range(L)) <= Allocated[o], name="resource_allocation_o{}".format(o))

# Add constraints for total input resources used by the processes for each option not exceeding the allocated resources
for o in range(O):
    model.addConstr(gp.quicksum(Input[l, o] * ProcessExecutions[l] for l in range(L)) <= Allocated[o], name=f"resource_limit_option_{o}")

# Outputs produced for all price points from executing processes do not exceed demand
for p in range(P):
    model.addConstr(gp.quicksum(ProcessExecutions[l] * Output[l, p] for l in range(L)) <= Demand[p], name=f"output_vs_demand_p{p}")

