# Define the objective function
model.setObjective(
    gp.quicksum(
        (gp.quicksum(Price[p] * ProcessExecutions[l] * Output[l, p] for p in range(P)) -
         Cost[l] * ProcessExecutions[l]
        ) for l in range(L)
    ),
    gp.GRB.MAXIMIZE
)
