# Add data rate constraint for each destination
for d in range(NumFlowReqs):
    model.addConstr(gp.quicksum(Flow[i, d] for i in range(NumLinks) if EndNode[i] == Destination[d]) >= DataRate[d], name="data_rate_requirement_d{}".format(d))

# Constraint: Flow conservation at intermediate nodes for each flow requirement
for k in range(NumFlowReqs):
    for n, value in enumerate(EndNode):
        # Check if n is not a source or destination for flow k
        if n != Source[k] and n != Destination[k]:
            incoming_flow = gp.quicksum(Flow[i, k] for i, j in enumerate(EndNode) if j == n)
            outgoing_flow = gp.quicksum(Flow[l, k] for l, j in enumerate(StartNode) if j == n)
            model.addConstr(incoming_flow == outgoing_flow, name=f"flow_conservation_node_{n}_req_{k}")
